export const typography = {
  fontFamily: [
    "Helvetica Neue",
    "Arial",
    "Hiragino Kaku Gothic ProN",
    "Hiragino Sans",
    "Meiryo",
    "sans-serif",
    "sans-serif",
  ],
  variants: {
    title1: {
      fontSize: 28,
      fontWeight: 600,
