      lineHeight: 1.3,
    },
    title2: {
      fontSize: 24,
      fontWeight: 600,
      letterSpacing: 0.25,
      lineHeight: 1.3,
    },
    title3: {
      fontSize: 20,
      fontWeight: 600,
      letterSpacing: 0.15,
      lineHeight: 1.3,
    },
    subtitle1: {
      fontSize: 17,
