    subtitle2: {
      fontSize: 14,
      fontWeight: 600,
      letterSpacing: 0.1,
      lineHeight: 1.3,
    },
    body1: {
      fontSize: 17,
      fontWeight: 400,
      letterSpacing: 0.5,
      lineHeight: 1.5,
    },
    body2: {
      fontSize: 14,
      fontWeight: 400,
      letterSpacing: 0.25,
      lineHeight: 1.5,
    },
    button: {
      fontSize: 14,
      fontWeight: 600,
      letterSpacing: 1,
      lineHeight: 1.3,
    },
    caption: {
      fontSize: 12,
      fontWeight: 400,
      letterSpacing: 0.4,
      lineHeight: 1.3,
    },
    overline: {
      fontSize: 12,
      fontWeight: 600,
      letterSpacing: 1,
      lineHeight: 1.3,
    },
  },
} as const
