import { createTheme } from "@mui/material"
import { fontWeight } from "@mui/system"

// フォントを設定
const fontFamily = [
  "Noto Sans JP",
  "メイリオ",
  "ＭＳ Ｐゴシック",
  "sans-serif",
  // 使用したいフォントを以降に羅列してください
].join(",")

const theme = createTheme({
  typography: {
    fontFamily: fontFamily, // フォント
    h1: {
      fontSize: 27,
    },
    h2: {
      fontSize: 24,
    },
    h3: {
      fontSize: 22,
    },
    subtitle1: {
      fontSize: 21,
      fontWeight: 600,
    },
    subtitle2: {
      fontSize: 17,
      fontWeight: 600,
    },
  },
  palette: {
    // Primaryカラーを設定
    primary: {
      light: "#54C527",
      main: "#ff9800",
      dark: "#b26a00",
      contrastText: "#000000",
    },
    // Secondaryカラーを設定
    secondary: {
      light: "#33eb91",
      main: "#00e676",
      dark: "#00a152",
      contrastText: "#ffffff",
    },
  },
  mixins: {
    // ツールバーの高さ
    toolbar: {
      minHeight: 64,
    },
  },
})

export default theme
