import React from "react"
import kyoto from "assets/images/shorenin.jpg"
import { ButtonBase, Grid, Typography } from "@mui/material"
import { useTranslation } from "react-i18next"
import { useNavigate } from "react-router-dom"
import styled from "@emotion/styled"

function About(): React.ReactElement {
  const navigate = useNavigate()
  const { t } = useTranslation()

  const CoverImage = () => {
    return <img src={kyoto} alt={"Kyoto"} style={{ maxWidth: "100%" }} />
  }

  const SNSButton = (color: string, title: string, url: string) => {
    const StyledButton = styled(ButtonBase)`
      padding: 16;
      width: 100%;
      background-color: ${color};
    `
    const StyledTypography = styled(Typography)`
      color: #fff;
    `
    return (
      <StyledButton onClick={() => window.open(url)}>
        <StyledTypography variant="button">{title}</StyledTypography>
      </StyledButton>
    )
  }
  const TwitterButton = () => {
    return (
      <ButtonBase
        onClick={() => {
          window.open("https://twitter.com/hiro_yuki_")
        }}
      >
        <Typography variant="button">{t("Twitter")}</Typography>
      </ButtonBase>
    )
  }
  const NoteButton = () => {
    return (
      <ButtonBase
        onClick={() => {
          window.open("https://note.mu/wecken")
        }}
      >
        <Typography variant="button">{t("Note")}</Typography>
      </ButtonBase>
    )
  }
  const ZennButton = () => {
    return (
      <ButtonBase
        onClick={() => {
          window.open("https://zenn.dev/wecken")
        }}
      >
        <Typography variant="button">{t("Zenn")}</Typography>
      </ButtonBase>
    )
  }
  const LinkedInnButton = () => {
    return (
      <ButtonBase
        onClick={() => {
          window.open("https://www.linkedin.com/in/hiroyuki-miura-836b4032/")
        }}
      >
        <Typography variant="button">{t("LinkedIn")}</Typography>
      </ButtonBase>
    )
  }
  return (
    <Grid
      container
      direction={"column"}
      alignItems={"center"}
      justifyContent={"flex-start"}
    >
      <CoverImage />
      <Grid
        padding={2}
        item
        direction={"column"}
        alignItems={"flex-start"}
        justifyContent={"flex-start"}
        style={{ width: "100%" }}
      >
        <Typography variant="h1">{t("About Wecken")}</Typography>
        <ButtonBase
          onClick={() => {
            navigate("/works")
          }}
        >
          <Typography variant="button">{t("Works")}</Typography>
        </ButtonBase>
        <Typography variant="h2">{t("Contacts")}</Typography>
        {SNSButton("#ff0000", "Twitter", "https://twitter.com/hiro_yuki_")}
        <TwitterButton />
        <NoteButton />
        <ZennButton />
        <LinkedInnButton />
      </Grid>
    </Grid>
  )
}

export default About
