import React, { VFC } from "react"
import { ButtonBase, Card, Grid, Link, Typography } from "@mui/material"
import styled from "@emotion/styled"

interface Props {
  title: string
  subtitle?: string
  body: string
  imageSrc: string
}

const MediaCard: VFC<Props> = ({ title, subtitle, body, imageSrc }) => {
  const StyledCard = styled(Card)`
    background-color: "white";
  `

  const StyledTypography = styled(Typography)`
    text-align: left;
  `
  const styles = {
    card: {},
    textContainer: {
      padding: "16px",
    },
  }
  const MbuxImage = () => {
    return <img src={imageSrc} alt="MBUX" style={{ maxWidth: "100%" }} />
  }
  const onClickCard = () => {
    alert("wei")
  }
  return (
    <ButtonBase onClick={onClickCard}>
      <StyledCard>
        <Grid>
          <MbuxImage />
          <Grid style={styles.textContainer}>
            <StyledTypography variant="h4">{title}</StyledTypography>
            {subtitle && (
              <StyledTypography variant="subtitle2">2021</StyledTypography>
            )}
            <StyledTypography variant="body2">{body}</StyledTypography>
          </Grid>
        </Grid>
      </StyledCard>
    </ButtonBase>
  )
}

export default MediaCard
